package main;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World");
		add(10,20);

	}
	public static void add(int n1,int n2){
		System.out.println(n1+n2);
	}
	public static void sub(int n1,int n2){
		System.out.println(n2-n1);
	}
	public static void prod(int n1,int n2){
		System.out.println(n2*n1);
	}
	public static void div(int n1){
		System.out.println(n1/n1);
	}

	
	public static void square(int n1){
		System.out.println(n1*n1);

	}
	public static void cube(int n1){
		System.out.println(n1*n1*n1);
	}
	public static void mod(int n1){
		System.out.println(n1%n1);
	}
	public static void br1(int n1){
		System.out.println(n1*n1);
	}

	public static void mas1(int n1){
		System.out.println(n1*n1);
	}
}
